import React from 'react';
import { Card, Button} from 'react-bootstrap';
import './Newsletter.css'
import Img from './Newsletter.svg'

var sectionStyle = {

  backgroundImage: {Img}
};

function Newsletter() {
    return (

    <div>


<Card style={sectionStyle}>
<Card.Body className="latest-wrapper lf-padding" style={{padding: "100px"}}>
    <Card.Title className="latest-area latest-height d-flex align-items-center caption"><h2>Get our latest deals and promotions</h2></Card.Title>
    <Card.Text className="latest-caption">
      Subscribe to our newsletter
    </Card.Text>
    <form action="#">
                        <input type="email" placeholder="Your email here"></input>

    <Button variant="primary">Subscribe</Button>
                    </form>
  </Card.Body>
</Card>

</div>
    )
}


export default Newsletter;
